import { Button, Empty, Image } from 'antd'
import { errorToast, formatTime, httpIsSuccess } from 'components/common/utils'
import { upload_file_api } from 'http/Common'
import React, { ChangeEvent, useRef, useState } from 'react'
import WordLogo from 'images/wordLogo.png'
import 'styles/pages/editCase.scss'
import { AgencyWordConfig } from 'pages/editCase'
const stylePrefix = 'page-editCase'

interface UploadAgencyConfig {
    agencyWord: AgencyWordConfig | null;
    setAgencyWord: React.Dispatch<React.SetStateAction<AgencyWordConfig | null>>;
}

export default function UploadAgency({ agencyWord, setAgencyWord }: UploadAgencyConfig) {
    const fileRef = useRef<HTMLInputElement>(null)
    const [loading, setLoading] = useState(false)
    const handleUpload = () => {
        fileRef.current?.click()
    }
    const handleChange = async (e: ChangeEvent<HTMLInputElement>) => {
        const files = e.target.files
        if (files) {
            setLoading(true)
            const file = files[0];
            const formData = new FormData()
            formData.append(file.name, file)
            const res = await upload_file_api(formData);
            if (httpIsSuccess(res.code)) {
                setAgencyWord({
                    url: res.data,
                    filename: file.name,
                    uploadTime: formatTime(new Date())
                })
            } else {
                errorToast(res.message)
            }
            setLoading(false)
        }
    }
    return (
        <div className={`${stylePrefix}-input-layout`}>
            <p>
                <span
                    className={`${stylePrefix}-title`}
                    style={{
                        lineHeight: '130px'
                    }}
                >代理词</span>
                <span className={`${stylePrefix}-symbol`} >：</span>
            </p>
            <div className={`${stylePrefix}-agency-word`}>
                {
                    agencyWord
                        ? <div className={`${stylePrefix}-agency-word-main`}>
                            <Image
                                width={130}
                                height={130}
                                src={WordLogo}
                                fallback="data:image/png;base64,"
                            />
                            <div>
                                <p className={`${stylePrefix}-agency-word-info`}>
                                    <span className={`${stylePrefix}-agency-word-title`}>文件名：</span>
                                    <span>{agencyWord.filename}</span>
                                </p>
                                <p className={`${stylePrefix}-agency-word-info`}>
                                    <span className={`${stylePrefix}-agency-word-title`}>上传时间：</span>
                                    <span>{agencyWord.uploadTime}</span>
                                </p>
                            </div>
                        </div>
                        : <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} />
                }
                <input
                    type="file"
                    ref={fileRef}
                    accept='.doc,.docx'
                    onChange={handleChange}
                    className={`${stylePrefix}-agency-word-input`}
                />
                <Button
                    className={`${stylePrefix}-agency-word-button`}
                    onClick={handleUpload}
                >{loading ? '上传中' : '上传'}</Button>
            </div>
        </div>
    )
}
